def initialize_session_states():
    if "button" not in st.session_state:
        st.session_state["button"] = False
    if "choice" not in st.session_state:
        st.session_state["choice"] = None
    if "up_down" not in st.session_state:
        st.session_state["up_down"] = None
    if "res" not in st.session_state:
        st.session_state["res"] = None

initialize_session_states()




# ------------------------------- Streamlit ------------------------------

st.set_page_config(
    page_title="ForetMagique",
    page_icon="üå≤",
    layout="centered"
)

hasClicked = card(
  title="Play ForetMagique?",
  text="",
  image="data:image/jpeg;base64,",
)
names = []
nb_of_players = 0
if hasClicked:
    nb_of_players = int(st.text_input("How many players?", 2, key=0))
    st.text(f"you are {nb_of_players} players for this game.")

    for i in range(1,int(nb_of_players)+1):
        name = st.text_input(f"What is the name of the player {i}?",key=i)
        names.append(name.capitalize())

# ------------------------- Initialising players -------------------------
ready = False
winner = {}
if len(names) == (nb_of_players) and len(names) !=0:
    if names[-1] != "":
        ready = True

if ready==True:
    left, middle, right = st.columns(3)
    wheel = middle.button("WHO STARTS THE GAME?", key="spin_btn", width="stretch")
    if wheel:
            # Setup for spinning animation
            total_spins = random.randint(20, 30)  # Total animation frames
            base_rotations = random.randint(8, 12)  # How many full rotations for needle
            winner_idx = random.randint(0, len(names) - 1)

            # Calculate where needle should stop (pointing to winner)
            segment_angle = 360 / len(names)
            final_needle_angle = (winner_idx * segment_angle) + (segment_angle / 2)
            total_needle_rotation = (base_rotations * 360) + final_needle_angle

            colors = plt.cm.Set3(np.linspace(0, 1, len(names)))

            # Create placeholder for animation
            wheel_placeholder = st.empty()

            # Spinning animation - only needle spins
            for i in range(total_spins):
                # Easing function - starts fast, slows down
                progress = i / total_spins
                eased_progress = 1 - (1 - progress) ** 3  # Ease out cubic
                current_needle_angle = eased_progress * total_needle_rotation

                fig, ax = plt.subplots(figsize=(8, 8))
                draw_spinning_wheel(ax, current_needle_angle, names, colors)

                with wheel_placeholder.container():
                    st.pyplot(fig)
                plt.close(fig)

                # Variable delay - slower as it progresses
                time.sleep(0.05 + (progress * 0.1))

            # Show winner
            winner = names[winner_idx]
            st.balloons()

if winner:
    game = Game(nb_of_players, names)
    res, game = initialize_game_and_players(game)
    df = pd.DataFrame(res)
    df.reset_index().to_csv("settings.csv", index=False)
    pd.DataFrame([winner], columns=["winner"]).to_csv("winner.csv", index=False)
    st.page_link("http://localhost:8507/", label="Start the game!")
